import React, { Component } from "react";
import {
  Form,
  Col,
  Row,
  Button,
} from "react-bootstrap";


class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: "",
      lname: "",
      gender: '',
      age:0,
      notes:'',
      image:null
    };
    this.eventChangehandler = this.eventChangehandler.bind(this);
  }
  componentWillMount() {
    this.setState({
      authFlag: false,
    });
  }

  eventChangehandler = (e) => {
    console.log('Event', e.target.name,'valye', e.target.value)
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  saveImage= (imageSrc)=>{
    console.log('Image', imageSrc)
    this.setState({
      image:imageSrc
    })
  }
  saveData = (e) => {
    e.preventDefault();
    console.log('Data saved first is now!!!!' , this.state)
    this.props.history.push('/healthVitals', {
      firstPageDetails: this.state
    })
  };
  render() {
    return (
      <div
        style={{
          display: "column",
          marginTop: "100px",
          justifyContent: "center",
        }}
      >
        <div
          style={{ display: "flex", flex: 1, width: "80%", marginLeft: "20px" }}
        >
          <div
            style={{
              flex: 3,
              borderRight: "1px solid rgb(6, 75, 96)",
              paddingRight: "5px",
            }}
          >
            <Form>
              <Form.Group as={Row} controlId="formHorizontalEmail">
                <Form.Label column sm={2}>
                  First Name:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="text"
                    name="fname"
                    placeholder="Enter first Name"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Last Name:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="text"
                    name="lname"
                    placeholder="Enter last Name"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group controlId="select">
                <Form.Label>Gender:</Form.Label>
                <Form.Control as="select" name='gender' custom onChange={this.eventChangehandler}>
                <option value='no selection'>Select</option>
                  <option value='male'>Male</option>
                  <option value='female'>Female</option>
                  <option value='others'>Others</option>
                </Form.Control>
              </Form.Group>

              <Form.Group as={Row} controlId="age">
                <Form.Label column sm={2}>
                  Age:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="age"
                    placeholder="Enter age in years"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group controlId="notes">
                <Form.Label>Notes or Other Details:</Form.Label>
                <Form.Control as="textarea" rows={3} name="notes"  onChange={this.eventChangehandler}/>
              </Form.Group>
            </Form>
          </div>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "50px",
          }}
        >
          <Button variant="primary" onClick={this.saveData}>SAVE</Button>
        </div>
      </div>
    );
  }
}
export default Home;
