import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router";
import { Card, Table } from "react-bootstrap";
import Webcam from "react-webcam";
import WebcamCapture from "./WebCam";

var db = openDatabase("healthcamp", "1.0", "My Web Sql", 50 * 1024 * 1024);

class Reports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      entries: [],
    };
  }
  componentWillMount() {
    let res = []
    let self = this
    db.transaction(function (tx) {
      tx.executeSql("SELECT firstname, lastname, age, image,  gender, medications, notes FROM patients", [], function (
        tx,
        results
      ) {
        console.log('Results', results)
        if (results.rows.length > 0) {
          for (var i = 0; i < results.rows.length; i++) {
            console.log(
              "Result -> " +
                results.rows.item(i).firstname +
                " " +
                results.rows.item(i).lastname
            );

            res.push({name:results.rows.item(i).firstname+results.rows.item(i).lastname,age:results.rows.item(i).age, gender: results.rows.item(i).gender, image: results.rows.item(i).image, medications:results.rows.item(i).medications, notes:results.rows.item(i).notes})
          }
        }
        self.setState({
            entries:res
        })
      });
    });

    console.log('This sql res', res)
  }
  usernameChangeHandler = (e) => {
    this.setState({
      username: e.target.value,
    });
  };

  saveData = (e) => {
    e.preventDefault();
    console.log("Data saved is", this.state);
  };

  passwordChangeHandler = (e) => {
    this.setState({
      password: e.target.value,
    });
  };

  submitLogin = (e) => {
    var headers = new Headers();
    //prevent page from refresh
    e.preventDefault();
    const data = {
      username: this.state.username,
      password: this.state.password,
    };

    this.usernameDisp.value = "";
    this.pwdDisp.value = "";
  };
  render() {
    return (
      <div style={{ display: "flex", marginTop: "100px" }}>
        <div style={{ marginLeft: "10px", marginRight: "10px" }}>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Gender</th>
                <th>Photo</th>
                <th>Medications</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
              {this.state.entries.map((el)=>{

                    return (<tr>
                        <td>{el.name}</td>
                        <td>{el.age}</td>
                        <td>{el.gender}</td>
                        <td>{el.image}</td>
                        <td>{el.medications}</td>
                    <td>{el.notes}</td>
                      </tr>)
              })}
            </tbody>
          </Table>
        </div>
      </div>
    );
  }
}
//export Login Component
export default Reports;
