import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router";
import { Button, Card, Table } from "react-bootstrap";
import { toast } from "react-toastify";
import { BsArrowRepeat } from "react-icons/bs";

var db = openDatabase("healthcamp", "1.0", "My Web Sql", 50 * 1024 * 1024);

class Reports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      entries: [],
    };
  }
  componentWillMount() {
    let res = []
    let self = this
    db.transaction(function (tx) {
      tx.executeSql("SELECT firstname, lastname, age, image,  gender, medications, notes FROM patients", [], function (
        tx,
        results
      ) {
        console.log('Results', results)
        if (results.rows.length > 0) {
          for (var i = 0; i < results.rows.length; i++) {
            res.push({name:results.rows.item(i).firstname+results.rows.item(i).lastname,age:results.rows.item(i).age, gender: results.rows.item(i).gender, image: results.rows.item(i).image, medications:results.rows.item(i).medications, notes:results.rows.item(i).notes})
          }
        }
        self.setState({
            entries:res
        })
      });
    });

    console.log('This sql res', res)
  }

  flushData = (res,ids)=>{

    axios.defaults.withCredentials = true;
    console.log('Online pushing data to backend')
  //  let fObj = this.props.location.state.firstPageDetails;
    let data = {data:res}
    console.log("Final Data to be sent to backend", data)
    console.log('Idfs are in', ids)
  let self = this

        axios.post('http://localhost:3001/saveData',data)
        .then(response => {            
            console.log("Status Code : ",response.status);
            let y= 'Y'
            let n = 'N'
            db.transaction(function (tx) {
              tx.executeSql('update patients set status = ? where status = ?', [y,n], function (
                tx,
                results
              ) {
                console.log('Results', results)
                toast.success(`Data synced successfully with backend`, {
                  position: "top-center",
                  autoClose: 5000,
                  hideProgressBar: false,
                  closeOnClick: true,
                  pauseOnHover: true,
                  draggable: true,
                  progress: undefined,
                });
             // console.log('Data saved is' , this.state)
              });
            });
            // bookAdded=true;  
        })
        .catch(response => {
            console.log("error", response)
        })
  }


    syncData = ()=>{
      let res=[]
      let ids=[]
      let self = this
      console.log('hre rin the transfer data backend')
      db.transaction(function (tx) {
        console.log('Starts')
        tx.executeSql('SELECT * FROM patients where status="'+'N'+'"',[], function (
          tx,
          results
        ) {
          console.log('Results here in the sunc', results)
          if (results.rows.length > 0) {
            for (var i = 0; i < results.rows.length; i++) {
              let res1=[]
              res1.push(results.rows.item(i).firstname,results.rows.item(i).lastname,results.rows.item(i).age,results.rows.item(i).gender,results.rows.item(i).notesFirst,results.rows.item(i).height,results.rows.item(i).weight,results.rows.item(i).temp,results.rows.item(i).pulse,results.rows.item(i).bp, results.rows.item(i).medications, results.rows.item(i).notes)
              res.push(res1)
              ids.push(results.rows.item(i).firstname)
            }
            console.log("Result herrere------------>", res)
            self.flushData(res,ids)
          }
        });
      });
    }
  usernameChangeHandler = (e) => {
    this.setState({
      username: e.target.value,
    });
  };

  saveData = (e) => {
    e.preventDefault();
    console.log("Data saved is", this.state);
  };

  passwordChangeHandler = (e) => {
    this.setState({
      password: e.target.value,
    });
  };

  submitLogin = (e) => {
    var headers = new Headers();
    //prevent page from refresh
    e.preventDefault();
    const data = {
      username: this.state.username,
      password: this.state.password,
    };

    this.usernameDisp.value = "";
    this.pwdDisp.value = "";
  };
  render() {
    return (
      <div style={{ display: "flex", flexDirection:'column', marginTop: "100px" , justifyContent:'center'}}>
        <div style={{display: "flex", justifyContent:'center'}}>
        <Button variant="success"  onClick={this.syncData}>Flush Data to Backend</Button>
        </div>
        <div style={{ marginLeft: "10px", marginTop:'50px', marginRight: "10px" }}>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Gender</th>
                <th>Photo</th>
                <th>Medications</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
              {this.state.entries.map((el)=>{

                    return (<tr>
                        <td>{el.name}</td>
                        <td>{el.age}</td>
                        <td>{el.gender}</td>
                        <td><img src={el.image} height='200px' width='200px'></img></td>
                        <td>{el.medications}</td>
                    <td>{el.notes}</td>
                      </tr>)
              })}
            </tbody>
          </Table>
        </div>
      </div>
    );
  }
}
//export Login Component
export default Reports;
