import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router";
import {
  Card,
  Modal,
  InputGroup,
  FormControl,
  Badge,
  Form,
  Col,
  Row,
  Button,
} from "react-bootstrap";
import Webcam from "react-webcam";
import WebcamCapture from "./WebCam";

class HealthVitals extends Component {
  constructor(props) {
    super(props);
    this.state = {
        height: 0,
        weight: 0,
        temp: 0,
        pulse:0,
        bp:0,
        medications:'',
        notes:''
      };
  }
  componentWillMount() {
    this.setState({
      authFlag: false,
    });
  }
  eventChangehandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });

  };

  saveData = (e) => {
    e.preventDefault();
    console.log('First Page Details', this.props.location.state.firstPageDetails)
    var db = openDatabase("healthcamp.db", '1.0', "My WebSQL Database", 2 * 1024 * 1024);
    db.transaction(function (tx) {
        tx.executeSql("CREATE TABLE IF NOT EXISTS patients (id integer primary key, firstname text, lastname text, gender text, age text, notesFirst text, height text, weight text, temp text, pulse text, bp text, medications text, notes text)");
    });
    console.log('Data saved is' , this.state)
}

  submitLogin = (e) => {
    var headers = new Headers();
    //prevent page from refresh
    e.preventDefault();
    const data = {
      username: this.state.username,
      password: this.state.password,
    };

    this.usernameDisp.value = "";
    this.pwdDisp.value = "";
  };
  render() {
    return (
      <div
        style={{
          display: "flex",
          marginTop: "100px",
          justifyContent: "center",
          flexDirection:'column'
        }}
      >
        <div
          style={{ display: "flex", flex: 1, width: "80%", marginLeft: "20px" }}
        >
          <div
            style={{
              flex: 3,
              borderRight: "1px solid rgb(6, 75, 96)",
              paddingRight: "5px",
            }}
          >
            <Form>
              <Form.Group as={Row} controlId="formHorizontalEmail">
                <Form.Label column sm={2}>
                  Height:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="height"
                    placeholder="Enter height in cms"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Weight:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="weight"
                    placeholder="Enter weight in kgs"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Body Temperature:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="decimal"
                    name="temp"
                    placeholder="Enter body temperature in Farenheit"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Pulse Rate:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="pulse"
                    placeholder="Enter pulse rate"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  BP:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="bp"
                    placeholder="Enter BP"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>
            </Form>
          </div>
          <div style={{ flex: 2, marginRight: "20px", paddingLeft: "10px" }}>
            <div style={{ display: "flex", flexDirection: "column" }}>
              <Form.Group controlId="notes">
                <Form.Label>Medications:</Form.Label>
                <Form.Control as="textarea" rows={3} name="medications" onChange={this.eventChangehandler} />
              </Form.Group>
              <Form.Group controlId="notes">
                <Form.Label>Notes:</Form.Label>
                <Form.Control as="textarea" rows={3} name="notes" onChange={this.eventChangehandler} />
              </Form.Group>
            </div>
          </div>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "50px",
          }}
        >
          <Button variant="primary" onClick={this.saveData}>SAVE</Button>
        </div>
      </div>
    );
  }
}
//export Login Component
export default HealthVitals;
