import React, { Component } from "react";
import {
  Form,
  Col,
  Row,
  Button,
} from "react-bootstrap";


class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: "",
      lname: "",
      gender: '',
      age:0,
      notes:'',
      image:null
    };
    this.eventChangehandler = this.eventChangehandler.bind(this);
  }
  componentWillMount() {
    this.setState({
      authFlag: false,
    });
  }

  eventChangehandler = (e) => {
    console.log('Event', e.target.name,'valye', e.target.value)
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  saveImage= (imageSrc)=>{
    console.log('Image', imageSrc)
    this.setState({
      image:imageSrc
    })
  }
  saveData = (e) => {
    e.preventDefault();
    console.log('Data saved first is now!!!!' , this.state)
  };
  render() {
    return (
      <div
        style={{
          display: "column",
          marginTop: "100px",
          justifyContent: "center",
        }}
      >
        <div
          style={{ display: "flex", flex: 1, width: "20%", justifyContent:'center' }}
        >
          <div
            style={{
              flex: 3,
              borderRight: "1px solid rgb(6, 75, 96)",
              paddingRight: "5px",
            }}
          >
            <Form>


              <Form.Group controlId="select">
                <Form.Label>Gender:</Form.Label>
                <Form.Control as="select" name='gender' custom onChange={this.eventChangehandler}>
                <option value='no selection'>Select</option>
                  <option value='male'>Male</option>
                  <option value='female'>Female</option>
                  <option value='others'>Others</option>
                </Form.Control>
              </Form.Group>
            </Form>
          </div>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "50px",
          }}
        >
          <Button variant="primary" onClick={this.saveData}>SAVE</Button>
        </div>
      </div>
    );
  }
}
export default Home;
