import React, { Component } from "react";
import {
  Form,
  Col,
  Row,
  Button,
  Badge
} from "react-bootstrap";
import TwitterLogin from "react-twitter-login";

let tweets=[{
user:'shubh',
tweet:'#lost 30 pounds in 3 weeks #pilatees '
},{
    user:'shubh',
    tweet:'#lost 30 pounds in 3 weeks #pilatees '  
}]
class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      goals: [],
      trends:[],
      trendSelected:'',
      image:null,
      goal:''
    };
    this.eventChangehandler = this.eventChangehandler.bind(this);
  }
  componentWillMount() {

    //API end point to
    // this.setState({
    //   goals: dummyOptions,
    // });
  }

  eventChangehandler = (e) => {
    console.log('Event', e.target.name,'valye', e.target.value)

    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  saveImage= (imageSrc)=>{
    console.log('Image', imageSrc)
    this.setState({
      image:imageSrc
    })
  }
  saveData = (e) => {
    e.preventDefault();

    console.log('Data saved goal and trend selected!!!!' , this.state.goal, this.state.trendSelected)
  };

  makeActive=(el)=>{
    let trends = this.state.trends;
    let trendSelected = el
    console.log('Here')
    for(var i=0;i<trends.length;i++)
    {
      if(trends[i].title==el)
      {
        trends[i].status = true;

      }
      else
      {
        trends[i].status = false;
      }
    }
    this.setState({
      trends: trends,
      trendSelected:trendSelected
    })
  }
  render() {
    console.log('This goals', this.state.goals)
    return (
      <div
        style={{
          display: "flex",
          flexDirection:'column',
          marginTop: "100px",
          justifyContent: "flex-start",
          alignItems:'center'
        }}
      >
        <div
          style={{ display: "flex", flexDirection:'row', justifyContent:'space-around'}}
        >
        <p>Goal:{this.props.location.state.goal}</p>  
        <p style={{marginLeft:'50px'}}>HashTag:{this.props.location.state.goal}</p> 
        </div>

        <h2>What's Trending?</h2>
        {

        }

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "50px",
          }}
        >
          <Button variant="primary" onClick={this.saveData}>Next</Button>
        </div>
      </div>
    );
  }
}
export default Dashboard;
