import React, { Component } from "react";
import {
  Form,
  Col,
  Row,
  Button,
  Badge
} from "react-bootstrap";
import TwitterLogin from "react-twitter-login";

let tweets=[{
user:'shubh',
tweet:'#lost 30 pounds in 3 weeks #pilatees '
},{
    user:'shubh',
    tweet:'#lost 30 pounds in 3 weeks #pilatees '  
}]
let groups=[{
    name:'shubh',
    tweet:'#lost 30 pounds in 3 weeks #pilatees '
    },{
        name:'shubh',
        tweet:'#lost 30 pounds in 3 weeks #pilatees '  
    }]
class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      goals: [],
      trends:[],
      trendSelected:'',
      image:null,
      goal:'',
      trend:true,
      support:false,
      track:false
    };
    this.eventChangehandler = this.eventChangehandler.bind(this);
  }
  componentWillMount() {

    //API end point to
    // this.setState({
    //   goals: dummyOptions,
    // });
  }

  eventChangehandler = (e) => {
    console.log('Event', e.target.name,'valye', e.target.value)

    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  saveImage= (imageSrc)=>{
    console.log('Image', imageSrc)
    this.setState({
      image:imageSrc
    })
  }
  saveData = (e) => {
    e.preventDefault();

    console.log('Data saved goal and trend selected!!!!' , this.state.goal, this.state.trendSelected)
  };

  makeActive=(el)=>{
if(el=='Trend')
{
    this.setState({
        trend:true,
        support:false,
        track:false
    })
}

else if(el=='Support')
{
    this.setState({
        trend:false,
        support:true,
        track:false
    })
}
else
{
    this.setState({
        trend:false,
        support:false,
        track:true
    })
}
  }
  render() {
    console.log('This goals', this.state.goals)
    return (
      <div
        style={{
          display: "flex",
          flexDirection:'column',
          marginTop: "100px",
          justifyContent: "flex-start",
          alignItems:'center'
        }}
      >
<div
          style={{ display: "flex", flexDirection:'row', justifyContent:'space-around'}}
        >

    <div style={{marginLeft:'10px',cursor: 'pointer'}}  onClick={()=>this.makeActive('Trend')}><h3>{!this.state.trend?<Badge variant="secondary">Trend</Badge>:<Badge variant="primary">Trend</Badge>}</h3></div>
    <div style={{marginLeft:'10px',cursor: 'pointer'}}  onClick={()=>this.makeActive('Support')}><h3>{!this.state.support?<Badge variant="secondary">Support</Badge>:<Badge variant="primary">Support</Badge>}</h3></div>
    <div style={{marginLeft:'10px',cursor: 'pointer'}}  onClick={()=>this.makeActive('Track')}><h3>{!this.state.track?<Badge variant="secondary">Track</Badge>:<Badge variant="primary">Track</Badge>}</h3></div>

        </div>


        <div
          style={{ display: "flex", flexDirection:'row', justifyContent:'space-around'}}
        >
        <p>Goal:<b>{this.props.location.state.goal}</b></p>  
        <p style={{marginLeft:'50px'}}>HashTag:<b>{this.props.location.state.goal}</b></p> 
        </div>

        <h2>What's Trending?</h2>

      {this.state.trend? tweets.map((el)=>{
          return  <div style={{display:"flex", backgroundColor:'lightgray', height:'60px', width:'80%', alignItems:'center', marginTop:'30px'}}>
          <div style={{marginLeft:'30px'}}><p>{el.user} {el.tweet} </p></div>
              </div>
      }) :
          (this.state.support?<div style={{display:'flex',flexDirection:"row", width:'80%', justifyContent:'flex-start', alignItems:'center'}}>

        <div style={{display:'flex', flexDirection:'column', flex:2, width:'100%', height:'60px'}}>
      <div style={{flexDirection:'column', border: "1px solid rgb(6, 75, 96)"}}>
      <p>Support Groups</p>
      {groups.map((el)=>{
          return  <div style={{display:"flex", backgroundColor:'lightgray', width:'80%', alignItems:'center', justifyContent:'space-between', marginTop:'30px'}}>
          <div style={{marginLeft:'30px'}}><p>{el.name}</p></div>
          <div>
          <Button variant="primary" onClick={this.saveData}>Follow</Button>
          </div>
              </div>
      })}

      </div>
        </div>
        <div style={{display:'flex', flexDirection:'column', flex:1,  border: "1px solid rgb(6, 75, 96)"}}>

        <div style={{flexDirection:'column',borderWidth:'2px', border: "1px solid rgb(6, 75, 96)"}}>
        <p>Support Chat</p>
        </div>
</div>

          </div>:<p>track</p>)
      }


        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "50px",
          }}
        >
          <Button variant="primary" onClick={this.saveData}>Next</Button>
        </div>
      </div>
    );
  }
}
export default Dashboard;
