import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router";
import {
    Card,
    Modal,
    InputGroup,
    FormControl,
    Badge,
    Form,
    Col,
    Row,
    Button,
  } from "react-bootstrap";

//Define a Login Component
class Demographics extends Component {
  //call the constructor method
  constructor(props) {
    //Call the constructor of Super class i.e The Component
    super(props);
    //maintain the state required for this component
    this.state = {
      username: "",
      password: "",
      authFlag: false,
    };
    //Bind the handlers to this class
    this.usernameChangeHandler = this.usernameChangeHandler.bind(this);
    this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
    this.submitLogin = this.submitLogin.bind(this);
  }
  //Call the Will Mount to set the auth Flag to false
  componentWillMount() {
    this.setState({
      authFlag: false,
    });
  }
  //username change handler to update state variable with the text entered by the user
  usernameChangeHandler = (e) => {
    this.setState({
      username: e.target.value,
    });
  };
  //password change handler to update state variable with the text entered by the user
  passwordChangeHandler = (e) => {
    this.setState({
      password: e.target.value,
    });
  };
  //submit Login handler to send a request to the node backend
  submitLogin = (e) => {
    var headers = new Headers();
    //prevent page from refresh
    e.preventDefault();
    const data = {
      username: this.state.username,
      password: this.state.password,
    };
    //set the with credentials to true
    // axios.defaults.withCredentials = true;
    // //make a post request with the user data
    // axios
    //   .post("http://localhost:3001/login", data)
    //   .then((response) => {
    //     console.log("Status Code : ", response.status);
    //     this.setState({
    //       authFlag: true,
    //     });
    //   })
    //   .catch((response) => {
    //     alert("Invalid Login Credentials :(");
    //     this.setState({
    //       authFlag: false,
    //       username: "",
    //       password: "",
    //     });
    //   });
    this.usernameDisp.value = "";
    this.pwdDisp.value = "";
    // {<Route exact path="/login" component={Login}/>}
  };
  render() {

    return (
      <div style={{display:'flex', marginTop:'100px', justifyContent:'center' }}>
        <div style={{display:'flex', flex:1, border:'1px solid green', width:'80%', marginLeft:'20px'}}>
        <div style={{ flex: 3, border:'1px solid red' }}>
        <Form>
  <Form.Group as={Row} controlId="formHorizontalEmail">
    <Form.Label column sm={2}>
      First Name
    </Form.Label>
    <Col sm={10}>
      <Form.Control type="text" name='fname' placeholder="Enter first Name" />
    </Col>
  </Form.Group>

  <Form.Group as={Row} controlId="formHorizontalPassword">
  <Form.Label column sm={2}>
      last Name
    </Form.Label>
    <Col sm={10}>
      <Form.Control type="text" name='lname' placeholder="Enter last Name" />
    </Col>
  </Form.Group>

  <Form.Group controlId="select">
    <Form.Label>Gender</Form.Label>
    <Form.Control as="select" custom>
      <option>Male</option>
      <option>Female</option>
      <option>Others</option>
    </Form.Control>
  </Form.Group>

  <Form.Group as={Row} controlId="formHorizontalPassword">
  <Form.Label column sm={2}>
      Age
    </Form.Label>
    <Col sm={10}>
      <Form.Control type="number" name='age' placeholder="Enter age in years" />
    </Col>
  </Form.Group>


  <fieldset>
    <Form.Group as={Row}>
      <Form.Label as="legend" column sm={2}>
        Radios
      </Form.Label>
      <Col sm={10}>
        <Form.Check
          type="radio"
          label="first radio"
          name="formHorizontalRadios"
          id="formHorizontalRadios1"
        />
        <Form.Check
          type="radio"
          label="second radio"
          name="formHorizontalRadios"
          id="formHorizontalRadios2"
        />
        <Form.Check
          type="radio"
          label="third radio"
          name="formHorizontalRadios"
          id="formHorizontalRadios3"
        />
      </Col>
    </Form.Group>
  </fieldset>
  <Form.Group as={Row} controlId="formHorizontalCheck">
    <Col sm={{ span: 10, offset: 2 }}>
      <Form.Check label="Remember me" />
    </Col>
  </Form.Group>

  <Form.Group as={Row}>
    <Col sm={{ span: 10, offset: 2 }}>
      <Button type="submit">Sign in</Button>
    </Col>
  </Form.Group>
</Form>
        </div>
        <div style={{ flex: 2,border:'1px solid blue',marginRight:'20px'}}></div>
        </div>
      </div>
    );
  }
}
//export Login Component
export default Demographics;
