import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router";
import {
  Card,
  Modal,
  InputGroup,
  FormControl,
  Badge,
  Form,
  Col,
  Row,
  Button,
} from "react-bootstrap";
import Webcam from "react-webcam";
import WebcamCapture from "./WebCam";

class Demographics extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: "",
      lname: "",
      gender: '',
      age:0,
      notes:'',
      image:null
    };
    this.eventChangehandler = this.eventChangehandler.bind(this);
  }
  componentWillMount() {
    this.setState({
      authFlag: false,
    });
  }

  eventChangehandler = (e) => {
    console.log('Event', e.target.name,'valye', e.target.value)
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  saveImage= (imageSrc)=>{
    console.log('Image', imageSrc)
    this.setState({
      image:imageSrc
    })
  }
  saveData = (e) => {
    e.preventDefault();
    console.log('Data saved is' , this.state)
    this.props.history.push('/healthVitals', {
      myProp: this.props.myProp,
      foo: 'bar'
    })
    //set the with credentials to true
    // axios.defaults.withCredentials = true;
    // //make a post request with the user data
    // axios
    //   .post("http://localhost:3001/login", data)
    //   .then((response) => {
    //     console.log("Status Code : ", response.status);
    //     this.setState({
    //       authFlag: true,
    //     });
    //   })
    //   .catch((response) => {
    //     alert("Invalid Login Credentials :(");
    //     this.setState({
    //       authFlag: false,
    //       username: "",
    //       password: "",
    //     });
    //   });
  };
  render() {
    return (
      <div
        style={{
          display: "column",
          marginTop: "100px",
          justifyContent: "center",
        }}
      >
        <div
          style={{ display: "flex", flex: 1, width: "80%", marginLeft: "20px" }}
        >
          <div
            style={{
              flex: 3,
              borderRight: "1px solid rgb(6, 75, 96)",
              paddingRight: "5px",
            }}
          >
            <Form>
              <Form.Group as={Row} controlId="formHorizontalEmail">
                <Form.Label column sm={2}>
                  First Name:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="text"
                    name="fname"
                    placeholder="Enter first Name"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Last Name:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="text"
                    name="lname"
                    placeholder="Enter last Name"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group controlId="select">
                <Form.Label>Gender:</Form.Label>
                <Form.Control as="select" name='gender' custom onChange={this.eventChangehandler}>
                  <option value='male'>Male</option>
                  <option value='female'>Female</option>
                  <option value='others'>Others</option>
                </Form.Control>
              </Form.Group>

              <Form.Group as={Row} controlId="age">
                <Form.Label column sm={2}>
                  Age:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="age"
                    placeholder="Enter age in years"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group controlId="notes">
                <Form.Label>Notes or Other Details:</Form.Label>
                <Form.Control as="textarea" rows={3} name="notes"  onChange={this.eventChangehandler}/>
              </Form.Group>
            </Form>
          </div>
          <div style={{ flex: 2, marginRight: "20px" }}>
            <div>
              <h4 style={{ textAlign: "center" }}>Photo:</h4>
              <WebcamCapture saveImage={this.saveImage} />
            </div>
          </div>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "50px",
          }}
        >
          <Button variant="primary" onClick={this.saveData}>SAVE</Button>
        </div>
      </div>
    );
  }
}
export default Demographics;
