import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router";
import {
    Card,
    Modal,
    InputGroup,
    FormControl,
    Badge,
    Form,
    Col,
    Row,
    Button,
  } from "react-bootstrap";
import Webcam from "react-webcam";
import WebcamCapture from "./WebCam";

class Reports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      authFlag: false,
    };
    //Bind the handlers to this class
    this.usernameChangeHandler = this.usernameChangeHandler.bind(this);
    this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
    this.submitLogin = this.submitLogin.bind(this);
  }
  componentWillMount() {
    this.setState({
      authFlag: false,
    });
  }
  usernameChangeHandler = (e) => {
    this.setState({
      username: e.target.value,
    });
  };

  passwordChangeHandler = (e) => {
    this.setState({
      password: e.target.value,
    });
  };

  submitLogin = (e) => {
    var headers = new Headers();
    //prevent page from refresh
    e.preventDefault();
    const data = {
      username: this.state.username,
      password: this.state.password,
    };

    this.usernameDisp.value = "";
    this.pwdDisp.value = "";
  };
  render() {

    return (
      <div style={{display:'flex', marginTop:'100px', justifyContent:'center' }}>
        <div style={{display:'flex', flex:1, width:'80%', marginLeft:'20px'}}>
        <div style={{ flex: 3, borderRight:'1px solid rgb(6, 75, 96)', paddingRight:'5px' }}>
        <Form>
  <Form.Group as={Row} controlId="formHorizontalEmail">
    <Form.Label column sm={2}>
      Height:
    </Form.Label>
    <Col sm={10}>
      <Form.Control type="number" name='height' placeholder="Enter height in cms" />
    </Col>
  </Form.Group>

  <Form.Group as={Row} controlId="formHorizontalPassword">
  <Form.Label column sm={2}>
      Weight:
    </Form.Label>
    <Col sm={10}>
      <Form.Control type="number" name='weight' placeholder="Enter weight in kgs" />
    </Col>
  </Form.Group>


  <Form.Group as={Row} controlId="formHorizontalPassword">
  <Form.Label column sm={2}>
      Body Temperature:
    </Form.Label>
    <Col sm={10}>
      <Form.Control type="decimal" name='temp' placeholder="Enter body temperature in Farenheit" />
    </Col>
  </Form.Group>

  <Form.Group controlId="select">
    <Form.Label>Gender:</Form.Label>
    <Form.Control as="select" custom>
      <option>Male</option>
      <option>Female</option>
      <option>Others</option>
    </Form.Control>
  </Form.Group>

  <Form.Group as={Row} controlId="age">
  <Form.Label column sm={2}>
      Age:
    </Form.Label>
    <Col sm={10}>
      <Form.Control type="number" name='age' placeholder="Enter age in years" />
    </Col>
  </Form.Group>

  <Form.Group controlId="notes">
    <Form.Label>Notes or Other Details:</Form.Label>
    <Form.Control as="textarea" rows={3} name='notes'/>
  </Form.Group>
</Form>
        </div>
        <div style={{ flex: 2, marginRight:'20px'}}>
        <div>
        <h4 style={{textAlign:'center'}}>Photo:</h4>
        <WebcamCapture/>
        </div>


        </div>

        </div>
      </div>
    );
  }
}
//export Login Component
export default Reports;
