    Card,
    Modal,
    InputGroup,
    FormControl,
    Badge,
    Form,
    Col,
    Row,
    Table,
  } from "react-bootstrap";
import Webcam from "react-webcam";
import WebcamCapture from "./WebCam";

class Reports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      authFlag: false,
    };
