import React, { Component } from "react";
import {
  Form,
  Col,
  Row,
  Button,
} from "react-bootstrap";

const dummyOptions = ['Lose Weight', 'Rehabs','Job Seekers']
const dummyTrends = ['Dienting', 'Exercise']
class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: "",
      lname: "",
      gender: '',
      age:0,
      notes:'',
      image:null
    };
    this.eventChangehandler = this.eventChangehandler.bind(this);
  }
  componentWillMount() {
    this.setState({
      authFlag: false,
    });
  }

  eventChangehandler = (e) => {
    console.log('Event', e.target.name,'valye', e.target.value)
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  saveImage= (imageSrc)=>{
    console.log('Image', imageSrc)
    this.setState({
      image:imageSrc
    })
  }
  saveData = (e) => {
    e.preventDefault();
    console.log('Data saved first is now!!!!' , this.state)
  };
  render() {
    return (
      <div
        style={{
          display: "flex",
          flexDirection:'column',
          marginTop: "100px",
          justifyContent: "flex-start",
        }}
      >
        <div
          style={{ display: "flex", flexDirection:'row', justifyContent:'center'}}
        >
          
            <Form>
              <Form.Group controlId="select">
                <Form.Label>Goal:</Form.Label>
                <Form.Control as="select" name='gender' custom onChange={this.eventChangehandler}>
                <option value='no selection'>Select</option>
                  <option value='male'>Losing Weight</option>
                  <option value='female'>Rehab</option>
                  <option value='others'>Job Seekers</option>
                </Form.Control>
              </Form.Group>
            </Form>
          
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "50px",
          }}
        >
          <Button variant="primary" onClick={this.saveData}>SAVE</Button>
        </div>
      </div>
    );
  }
}
export default Home;
