import React, { Component } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import {
  Form,
  Col,
  Row,
  Button,
} from "react-bootstrap";
var db = openDatabase("healthcamp", "1.0", "My Web Sql", 50 * 1024 * 1024);
class HealthVitals extends Component {
  constructor(props) {
    super(props);
    this.state = {
      height: 0,
      weight: 0,
      temp: 0,
      pulse: 0,
      bp: 0,
      medications: "",
      notes: "",
    };
  }
  componentWillMount() {

  }
  eventChangehandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  flushData = (res, ids) => {
    axios.defaults.withCredentials = true;
    let data = { data: res };
    let self = this;

    axios
      .post("http://localhost:3001/saveData", data)
      .then((response) => {
        console.log("Status Code : ", response.status);
        let y = "Y";
        let n = "N";
        db.transaction(function (tx) {
          tx.executeSql(
            "update patients set status = ? where status = ?",
            [y, n],
            function (tx, results) {
              console.log("Results", results);
              toast.success(`Data synced successfully with backend`, {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
              });
              self.props.history.push("/reports", {});
              // console.log('Data saved is' , this.state)
            }
          );
        });
        // bookAdded=true;
      })
      .catch((response) => {
        console.log("error", response);
      });
  };

  syncData = () => {
    let res = [];
    let ids = [];
    let self = this;
    console.log("hre rin the transfer data backend");
    db.transaction(function (tx) {
      console.log("Starts");
      tx.executeSql(
        'SELECT * FROM patients where status="' + "N" + '"',
        [],
        function (tx, results) {
          console.log("Results here in the sunc", results);
          if (results.rows.length > 0) {
            for (var i = 0; i < results.rows.length; i++) {
              let res1 = [];
              res1.push(
                results.rows.item(i).firstname,
                results.rows.item(i).lastname,
                results.rows.item(i).age,
                results.rows.item(i).gender,
                results.rows.item(i).notesFirst,
                results.rows.item(i).height,
                results.rows.item(i).weight,
                results.rows.item(i).temp,
                results.rows.item(i).pulse,
                results.rows.item(i).bp,
                results.rows.item(i).medications,
                results.rows.item(i).notes
              );
              res.push(res1);
              ids.push(results.rows.item(i).firstname);
            }
            console.log("Result herrere------------>", res);
            self.flushData(res, ids);
          }
        }
      );
    });
  };

  saveData = (e) => {
    e.preventDefault();
    console.log(
      "First Page Details",
      this.props.location.state.firstPageDetails
    );
    let fObj = this.props.location.state.firstPageDetails;
    let self = this.state;
    let thisSelf = this;
    db.transaction(function (tx) {
      tx.executeSql(
        "CREATE TABLE if not exists patients (_id integer primary key, firstname text, lastname text, gender text, image text, age text, notesFirst text, height text, weight text, temp text, pulse text, bp text, medications text, notes text, status text)"
      );
      let statusNewRecord = "N";
      tx.executeSql(
        "INSERT INTO patients (firstname, lastname, gender, image, age, notesFirst, height, weight, temp, pulse, bp, medications, notes, status) VALUES (?,?,?,?,?,?,?,?,?,?,?,?,?,?)",
        [
          fObj.fname,
          fObj.lname,
          fObj.gender,
          fObj.image,
          fObj.age,
          fObj.notes,
          self.height,
          self.weight,
          self.temp,
          self.pulse,
          self.bp,
          self.medications,
          self.notes,
          statusNewRecord,
        ]
      );

      alert("Data saved successfully");
    });
  };

  render() {
    return (
      <div
        style={{
          display: "flex",
          marginTop: "100px",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <div
          style={{ display: "flex", flex: 1, width: "80%", marginLeft: "20px" }}
        >
          <div
            style={{
              flex: 3,
              borderRight: "1px solid rgb(6, 75, 96)",
              paddingRight: "5px",
            }}
          >
            <Form>
              <Form.Group as={Row} controlId="formHorizontalEmail">
                <Form.Label column sm={2}>
                  Height:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="height"
                    placeholder="Enter height in cms"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Weight:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="weight"
                    placeholder="Enter weight in kgs"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Body Temperature:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="decimal"
                    name="temp"
                    placeholder="Enter body temperature in Farenheit"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Pulse Rate:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="pulse"
                    placeholder="Enter pulse rate"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  BP:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="bp"
                    placeholder="Enter BP"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>
            </Form>
          </div>
          <div style={{ flex: 2, marginRight: "20px", paddingLeft: "10px" }}>
            <div style={{ display: "flex", flexDirection: "column" }}>
              <Form.Group controlId="notes">
                <Form.Label>Medications:</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  name="medications"
                  onChange={this.eventChangehandler}
                />
              </Form.Group>
              <Form.Group controlId="notes">
                <Form.Label>Notes:</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  name="notes"
                  onChange={this.eventChangehandler}
                />
              </Form.Group>
            </div>
          </div>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            flexDirection: "row",
            marginTop: "50px",
            alignItems: "space-between",
          }}
        >
          <Button
            variant="primary"
            style={{ marginLeft: "20px" }}
            onClick={this.saveData}
          >
            SAVE
          </Button>

          <Button
            variant="primary"
            style={{ marginLeft: "20px" }}
            onClick={this.syncData}
          >
            Sync
          </Button>

          <Button
            variant="primary"
            style={{ marginLeft: "20px" }}
            onClick={() => {
              this.props.history.push("/reports", {});
            }}
          >
            Next
          </Button>
        </div>
      </div>
    );
  }
}
export default HealthVitals;
