import React, { Component } from "react";
import axios from "axios";
import { Button, Card, Table } from "react-bootstrap";
import { BsArrowRepeat } from "react-icons/bs";

var db = openDatabase("healthcamp", "1.0", "My Web Sql", 50 * 1024 * 1024);

class Reports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      entries: [],
    };
  }
  componentWillMount() {
    let res = [];
    let self = this;
    db.transaction(function (tx) {
      tx.executeSql(
        "SELECT firstname, lastname, age, image,  gender, medications, notes FROM patients",
        [],
        function (tx, results) {
          console.log("Results", results);
          if (results.rows.length > 0) {
            for (var i = 0; i < results.rows.length; i++) {
              res.push({
                name:
                  results.rows.item(i).firstname +
                  results.rows.item(i).lastname,
                age: results.rows.item(i).age,
                gender: results.rows.item(i).gender,
                image: results.rows.item(i).image,
                medications: results.rows.item(i).medications,
                notes: results.rows.item(i).notes,
              });
            }
          }
          self.setState({
            entries: res,
          });
        }
      );
    });
  }

  flushData = (res) => {
    axios.defaults.withCredentials = true;
    let data = { data: res };
    let self = this;
    axios
      .post("http://localhost:3001/saveData", data)
      .then((response) => {
        let yes = "Y";
        let no = "N";
        db.transaction(function (tx) {
          tx.executeSql(
            "update patients set status = ? where status = ?",
            [yes, no],
            function (tx, results) {
              console.log("Results", results);
            }
          );
        });
      })
      .catch((response) => {
        console.log("error", response);
      });
  };

  syncData = () => {
    if(navigator.onLine)
    {
      window.alert("Data flushed to backend sucessfully!");
    let res = [];
    let self = this;
    db.transaction(function (tx) {
      tx.executeSql(
        'SELECT * FROM patients where status="' + "N" + '"',
        [],
        function (tx, results) {
          if (results.rows.length > 0) {
            for (var i = 0; i < results.rows.length; i++) {
              let res1 = [];
              res1.push(
                results.rows.item(i).firstname,
                results.rows.item(i).lastname,
                results.rows.item(i).age,
                results.rows.item(i).gender,
                results.rows.item(i).notesFirst,
                results.rows.item(i).height,
                results.rows.item(i).weight,
                results.rows.item(i).temp,
                results.rows.item(i).pulse,
                results.rows.item(i).bp,
                results.rows.item(i).medications,
                results.rows.item(i).notes
              );
              res.push(res1);
            }
            self.flushData(res);
          }
        }
      );
    });
    }
    else{
      alert('No internet connectivity!. Data already saved to browser')
    }
  };

  render() {
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          marginTop: "100px",
          justifyContent: "center",
        }}
      >
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Button variant="success" onClick={this.syncData}>
            Sync Data <BsArrowRepeat />
          </Button>
        </div>
        <div
          style={{ marginLeft: "10px", marginTop: "50px", marginRight: "10px" }}
        >
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Name</th>
                <th>Age</th>
                <th>Gender</th>
                <th>Photo</th>
                <th>Medications</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
              {this.state.entries.map((el) => {
                return (
                  <tr>
                    <td>{el.name}</td>
                    <td>{el.age}</td>
                    <td>{el.gender}</td>
                    <td>
                      <img src={el.image} height="200px" width="200px"></img>
                    </td>
                    <td>{el.medications}</td>
                    <td>{el.notes}</td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
        </div>
      </div>
    );
  }
}
export default Reports;
