import React, { Component } from "react";
import { Redirect } from "react-router";
import axios from 'axios';
import {
  Card,
  Modal,
  InputGroup,
  FormControl,
  Badge,
  Form,
  Col,
  Row,
  Button,
} from "react-bootstrap";
var db = openDatabase('healthcamp', '1.0', 'My Web Sql', 50 * 1024 * 1024);
class HealthVitals extends Component {
  constructor(props) {
    super(props);
    this.state = {
        height: 0,
        weight: 0,
        temp: 0,
        pulse:0,
        bp:0,
        medications:'',
        notes:''
      };
  }
  componentWillMount() {
    this.setState({
      authFlag: false,
    });
  }
  eventChangehandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });

  };

  transferDataToBackend=()=>{

    db.transaction(function (tx) {
      tx.executeSql("SELECT * FROM patients where status="+'N', [], function (
        tx,
        results
      ) {
        console.log('Results', results)
        if (results.rows.length > 0) {
          for (var i = 0; i < results.rows.length; i++) {
            res.push({name:results.rows.item(i).firstname+results.rows.item(i).lastname,age:results.rows.item(i).age, gender: results.rows.item(i).gender, image: results.rows.item(i).image, medications:results.rows.item(i).medications, notes:results.rows.item(i).notes})
          }
        }
        self.setState({
            entries:res
        })
      });
    });
    axios.defaults.withCredentials = true;
    console.log('Online pushing data to backend')
    let fObj = this.props.location.state.firstPageDetails;
    let data = {fObj:fObj,...this.state}

    axios.post('http://localhost:3001/saveData',data)
    .then(response => {            
        console.log("Status Code : ",response.status);

        // bookAdded=true;  
    })
    .catch(response => {
        console.log("error", response)
    })

  }

  saveData = (e) => {
    e.preventDefault();
    console.log('First Page Details', this.props.location.state.firstPageDetails)
    let fObj = this.props.location.state.firstPageDetails;
    // var db = openDatabase("healthcamp.db", '1.0', "My WebSQL Database", 2 * 1024 * 1024);
    let self = this.state
    let thisSelf = this

    db.transaction(function(tx) {
        tx.executeSql('CREATE TABLE if not exists patients (_id integer primary key, firstname text, lastname text, gender text, image text, age text, notesFirst text, height text, weight text, temp text, pulse text, bp text, medications text, notes text, status text)');
        console.log("Navigator online status", navigator.onLine)
        tx.executeSql('INSERT INTO patients (firstname, lastname, gender, image, age, notesFirst, height, weight, temp, pulse, bp, medications, notes) VALUES (?,?,?,?,?,?,?,?,?,?,?,?,?,?)', [fObj.fname, fObj.lname, fObj.gender, fObj.image, fObj.age, fObj.notes, self.height, self.weight, self.temp, self.pulse, self.bp, self.medications, self.notes,'N']);
        if(navigator.onLine)
        {

          thisSelf.transferDataToBackend()
        }
    });

    this.props.history.push('/reports', {
      })
    console.log('Data saved is' , this.state)
}

  submitLogin = (e) => {
    var headers = new Headers();
    //prevent page from refresh
    e.preventDefault();
    const data = {
      username: this.state.username,
      password: this.state.password,
    };

    this.usernameDisp.value = "";
    this.pwdDisp.value = "";
  };
  render() {
    return (
      <div
        style={{
          display: "flex",
          marginTop: "100px",
          justifyContent: "center",
          flexDirection:'column'
        }}
      >
        <div
          style={{ display: "flex", flex: 1, width: "80%", marginLeft: "20px" }}
        >
          <div
            style={{
              flex: 3,
              borderRight: "1px solid rgb(6, 75, 96)",
              paddingRight: "5px",
            }}
          >
            <Form>
              <Form.Group as={Row} controlId="formHorizontalEmail">
                <Form.Label column sm={2}>
                  Height:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="height"
                    placeholder="Enter height in cms"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Weight:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="weight"
                    placeholder="Enter weight in kgs"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Body Temperature:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="decimal"
                    name="temp"
                    placeholder="Enter body temperature in Farenheit"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  Pulse Rate:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="pulse"
                    placeholder="Enter pulse rate"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formHorizontalPassword">
                <Form.Label column sm={2}>
                  BP:
                </Form.Label>
                <Col sm={10}>
                  <Form.Control
                    type="number"
                    name="bp"
                    placeholder="Enter BP"
                    onChange={this.eventChangehandler}
                  />
                </Col>
              </Form.Group>
            </Form>
          </div>
          <div style={{ flex: 2, marginRight: "20px", paddingLeft: "10px" }}>
            <div style={{ display: "flex", flexDirection: "column" }}>
              <Form.Group controlId="notes">
                <Form.Label>Medications:</Form.Label>
                <Form.Control as="textarea" rows={3} name="medications" onChange={this.eventChangehandler} />
              </Form.Group>
              <Form.Group controlId="notes">
                <Form.Label>Notes:</Form.Label>
                <Form.Control as="textarea" rows={3} name="notes" onChange={this.eventChangehandler} />
              </Form.Group>
            </div>
          </div>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "50px",
          }}
        >
          <Button variant="primary" onClick={this.saveData}>SAVE</Button>
        </div>
      </div>
    );
  }
}
//export Login Component
export default HealthVitals;
